import React from 'react'
import "./Share.css";
import { useState } from 'react'
import { createShare } from "../../store/share"

function Share() {
  const [userId, setUserId] = useState(0) //should be getting the user id from session. perhaps in controller
  const [content, setContent ] = useState('');
  const [share, setShare ] = useState({});

  const handleSubmit = (share) =>{
    setShare(userId,content)
    dispatch(createShare(share)) // no need to add share because it's included in create share?
  }

  return (
    <div className='form-wrapper'>
      <form className='form' onSubmit={handleSubmit}>
        <label>Spill the tea ...</label>
        <input type="textarea" onChange={(e) => setContent(e.target.value)}/>
        <button >Share</button>
      </form>
    </div>
  )
}

export default Share
