const uiReducer = (state = {}, action)=>{
  Object.freeze(state);
  const newState = {...state};
  switch (action.type){
    case ADD_MODAL:
      let result = modalsReducer(newState.modals, action);
      return {...newState,
      modals: result}
    default:
    return newState
  }
}

export const modalsReducer = (state = {}, action) => {
  console.log('not here yet')
  Object.freeze(state);
  const newState = { ...state };
  switch (action.type) {
    case TOGGLE_MODAL:
       console.log('WHAT',newState[action.id])
      newState[action.id].visible
        ? (newState[action.id].visible = false)
        : (newState[action.id].visible = true);
    return {...newState}
    default:
      return newState;
  }
};

export const preloadedModals = {
  ui: {
    modals: {
      1: {
        id: 1,
        visible: false,
      },
    },
  },
};



export default uiReducer;
