import React from 'react'
import "./NavBar.css"
import Home  from "../Home/index"
import About from "../About/index";
import Share from "../Share/index";
import NavRight from "../NavRight/index";
import { NavLink, Switch, Route , Redirect } from "react-router-dom"
import { removeCurrentUser } from '../../store/sessionReducer';
import { useSelector, useDispatch } from 'react-redux'
import Form from '../Form/Form';


function NavBar() {
  const dispatch = useDispatch();
  let buttonName;
  const currentUser = useSelector((state)=> state.session.user)
  console.log(first)
  if (currentUser) { 
    buttonName = 'Log In'
  } else {
    buttonName = 'Log Out'
  }
  return (
    <>
      <nav className="NavBar">
        <NavLink to="/">Home</NavLink>
        <NavLink to="/Share">Share</NavLink>
        <NavLink to="/About">About</NavLink>
        {buttonName === "Log In" && (
          <NavLink to={{ pathname: "/Form", state: { type: buttonName } }}>
            <button className="button">{buttonName}</button>
          </NavLink>
        )}
        {buttonName === "Log Out" && (
          <button className="button"
          onClick={()=>dispatch(removeCurrentUser(currentUser.id))}>{buttonName}</button>
        )}
      </nav>

      <NavRight />
      <Switch>
        <Route exact path="/" component={Home} />
        <Route exact path="/About" component={About} />
        <Route exact path="/Share" component={Share} />
        <Route exact path="/Form" component={Form} />
      </Switch>
    </>
  );
}

export default NavBar
