import React from "react";
import "./About.css";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { toggleModal } from "../../store/ui";

function About({visibility}) {
  console.log('props', visibility.visible)
  const ABOUT_KEY = 1;
  // const visible = useSelector((state) => state.ui.modals[ABOUT_KEY]);
  // const[visibility, setVisibility] = useState(false);
  const dispatch = useDispatch();


  useEffect(() => {
    // console.log("hidden", hidden);

   visibility.visible ? (hidden = "") : (hidden = "hidden");
  }, [visibility.visible]);

  const handleClick = (e) => {
 dispatch(toggleModal(ABOUT_KEY));
  };
  return (
    <>
      <div className={`about ${hidden}`} key={ABOUT_KEY}>
        <header>
          <i class="fa-regular fa-rectangle-xmark" onClick={handleClick}></i>
        </header>
        <div>
          Liam Noah Oliver Elijah James William Benjamin Lucas Henry Theodore
          Jack Levi Alexander Jackson Mateo Daniel Michael Mason Sebastian Ethan
        </div>
      </div>
    </>
  );
}

export default About;
