export const restoreCSRF = async () => {
  const result = await csrfFetch("/api/session");
  storeToken(result);
  return result;
};

export const storeToken = (response) => {
  const token = response.headers.get("X-CSRF-Token");
  if (token) sessionStorage.setItem("X-CSRF-Token", token);
};


export const restoreCurrentUser = (user) => {
  if (user) {
    sessionStorage.setItem("currentUser", JSON.stringify(user.id));

  } else {
    sessionStorage.setItem("currentUser", null);
  }
};






const csrfFetch = async (url, options = {}) => {
  options.method ||= "GET";
  options.headers ||= {};

  if (options.method.toUpperCase() !== "GET") {
    options.headers["Content-Type"] = "application/json";
    options.headers["Accept"] = "application/json";
    options.headers["X-CSRF-Token"] = sessionStorage.getItem("X-CSRF-Token");
  }
  const res = await fetch(url, options);
  if (res.status >= 400) throw res;
  return res;
};

export default csrfFetch;
