const uiReducer = (state = {}, action)=>{
  Object.freeze(state);
  const newState = {...state};
  switch (action.type){
    case ADD_MODAL:
      return {...newState,
      modals: modalsReducer(newState.modals,action)}
    default:
    return newState
  }
}

const modalsReducer = (state = {}, action) => {
  Object.freeze(state);
  const newState = { ...state };
  switch (action.type) {
    case TOGGLE_MODAL:
      newState.id.visible = true
    return {...newState}
    default:
      return newState;
  }
};

export const preloadedModals = {
  ui: {
    modals: {
        id: 1,
        visible: false,
      },

  },
};


export default uiReducer;
