import React from 'react'
import  { getRandomShare } from '../../store/share'
import { useState, useEffect } from 'react'
import { useDispatch } from "react-redux";



function Home() {
  const [share, setShare] = useState("");
  const dispatch = useDispatch();
  //  useEffect(()=>{
  //       const anon = async () => {
  //        const random = await dispatch(getRandomShare());
  //         setShare(random);
  //       }
  //       },[share])
  const handleClick = async (e) => {
    e.preventDefault();
    setShare("");
    console.log("are we here ever?");
    const random = await dispatch(getRandomShare());
    setShare(random);
    console.log("in handle submit - randomShare", random);
  };
  return (
    <div className="home">
      <button onClick={handleClick}>Generate Random Tea</button>
      {share && <div className="random-share">{share.content}</div>}
    </div>
  );
}

export default Home
