const TOGGLE_MODAL = 'TOGGLE_MODAL';
const ADD_MODAL = 'ADD_MODAL';

export const toggleModal = (id) =>({
  type: TOGGLE_MODAL,
  id
})
export const addModal = (id) =>({
  type: ADD_MODAL,
  id
})

