import csrfFetch from "./csrf";

const RECEIVE_CURRENT_USER = 'users/RECEIVE_CURRENT_USER'
const REMOVE_CURRENT_USER = 'users/REMOVE_CURRENT_USER'

export const receiveCurrentUser = user =>({
  type: RECEIVE_CURRENT_USER,
  user
})

export const removeCurrentUser = (userId) => ({
  type: REMOVE_CURRENT_USER,
  userId
});


export const loginUser =  (user) => async (dispatch) => {
    console.log("in login, pre-fetch", user);
   let res = await csrfFetch('/api/session',{
    method: 'POST',
    body: JSON.stringify(user)
   }).then((res) => res.json()).then((res) => dispatch(receiveCurrentUser(res)))
   .catch((err) => console.log(err));
}


export const sessionReducer = (state = {}, action) => {
  Object.freeze(state);
  const newState = { ...state }
  switch (action.type) {
    case RECEIVE_CURRENT_USER:
    return {...action.user}
    case REMOVE_CURRENT_USER:
    delete newState[action.user]
    return newState
    default:
      return newState;
  }
}
