import React from 'react'
import  getRandomShare  from '../../store/share'
import { useState } from 'react'
import { useDispatch } from "react-redux";


function Home() {

  const [share, setShare ] = useState('');

  const handleSubmit = async (e)=>{
    setShare
    e.preventDefault();
    const randomShare = await dispatch(getRandomShare());
    setShare(randomShare);

  }
  return (
    <div className='home'>
     <input type="submit" value="Generate Random Tea" onSubmit={handleSubmit}/>
     {(share) && <div className='random-share'>{share.content}</div> }
    </div>
  )
}

export default Home
