const ADD_SHARE = "ADD_SHARE";
const LOAD_SHARE = 'LOAD_SHARE';

export const addShare = (share) => ({
  type: ADD_SHARE,
  share,
});

export const loadShare = (share_id) =>({
  type: LOAD_SHARE,
  share_id
})

export const getShare = (share_id) => async (dispatch) => {
  const response = await fetch(`/api/shares/${share_id}`);
  if (response.ok) {
    const share = await response.json();
    dispatch(loadShare(share, share_id));
  }
}

export const createShare = (data, share_id = '1') => async (dispatch) => {
  const response = await fetch(`/api/shares/${share_id}`, {
    method: "POST",
    headers: {
      "Content-Type": 'application/json'
    },
    body: JSON.stringify(data)
  })
  if (response.ok) {
    const newShare = await response.json();
    dispatch(addShare(newShare));
  }
}

 let initialState = {};


export default function shareReducer(state = initialState, action) {

    const newState = { ...state };




  switch (action.type) {
    case ADD_SHARE:
      return newState;
    default:
      return newState;
  }
}
