import "./About.css";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { toggleModal } from "../../store/ui";

function About() {
  console.log('props', visibility)
  const ABOUT_KEY = 1;
  const visible = useSelector((state) => state.ui.modals[ABOUT_KEY]);
  // const[props, setVisibility] = useState(false);
  const dispatch = useDispatch();

     let hidden;
  useEffect(() => {
    // console.log("hidden", hidden);
   visibility.visible ? (hidden = "") : (hidden = "hidden");
  }, []);

  const handleClick = (e) => {
 dispatch(toggleModal(visibility.id));
  };
  return (
    <>
      <div className={`about ${hidden}`} >
        <header>
          <i class="fa-regular fa-rectangle-xmark" onClick={handleClick}></i>
        </header>
        <div>
          Liam Noah Oliver Elijah James William Benjamin Lucas Henry Theodore
          Jack Levi Alexander Jackson Mateo Daniel Michael Mason Sebastian Ethan
        </div>
      </div>
    </>
  );
}

export default About;
