import React from 'react'
import  getRandomShare  from '../../store/share'
import { useState, useEffect } from 'react'
import { useDispatch } from "react-redux";


function Home() {

  const [share, setShare ] = useState('');
  const dispatch = useDispatch();

  const handleClick = async (e)=>{
    e.preventDefault();
    setShare('');

    const randomShare = await dispatch(getRandomShare());
    setShare(randomShare);
    console.log('in handle submit - randomShare',randomShare)
  }
  return (
    <div className='home'>
     <button onClick={handleClick}>Generate Random Tea</button>
     {(share) && <div className='random-share'>{share.content}</div> }
    </div>
  )
}

export default Home
